// QUALQUER PESSOA PODE CHAMAR A FUNO BURN, MAS APENAS A API PODE CHAMAR A FUNO MINT
// Para trocar crdt por bcrdt
// 1 invoca a funo burn da BridgeEth para queimar o token na rede ethereum
//2 emite um evento para a api para avisar que o token foi queimado
// 3 api chama a funo mint
pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import './Itoken.sol';

contract BridgeBase {
  // admin address controlled by the bridge api
  address public admin;
  
  //  uma interface para o token crdt se for bridgeEth ou bcrdt se for BridgeBsc
  IToken public token;
  
  // evita que a mesma transao seja processada duas vezes.
  uint public nonce;
  
  // vai armazenar qual nonce foi processado
  mapping(uint => bool) public processedNonces;

  // fixa o processo se  burn ou mint
  enum Step { Burn, Mint }

  /* muito importante. So as informaes do evento. Vai dizer 
  qual endereo enviou, para onde, a quantidade, a data, o nonce da transao
  e se  uma transao de mint ou burn
  */
  event Transfer(
    address from,
    address to,
    uint amount,
    uint date,
    uint nonce,
    Step indexed step
  );

  /* o contrato bridge recebe no construtor o endereo do token que ser queimado ou mintado
  Se for BridgeEth ser o crdt, se for BridgeBsc ser bcrdt 
  */
  constructor(address _token) {
    admin = msg.sender;
    // instancia duas funes do token, ou seja mint e burn
    token = IToken(_token);
  }

// no pode confundir o argumento que a burn do bridge recebe com o burn do token
// vai ser chamada pelo script e emitir o evento Transfer que ser ouvido pela Api.
  // qualquer um pode chamar a funo burn
 // QUANDO FAZ O BURN EMITE O EVENTO TRANSFER
  function burn(address to, uint amount) external {
    
   // instanciao do token
   // token.burn recebe a quantidade que ser queimada e de quem  essa quantidade.
    token.burn(msg.sender, amount);
    
    emit Transfer(
      msg.sender,
      to,
      amount,
      block.timestamp,
      // cada transferncia ter um nico nonce
      nonce,
      Step.Burn
    );
    nonce++;
  }

 // apenas a bridge api deve poder chamar a funo mint. 
  function mint(address to, uint amount, uint otherChainNonce) external {
  require(msg.sender == admin, 'only admin');
  
  // verifica se o nonce no foi processado, se tiver true d um erro
  require(processedNonces[otherChainNonce] == false, 'transfer  processed');
  // atualiza o mapping dizendo que aquele nonce agora foi processado.
  
  processedNonces[otherChainNonce] = true;
    
    // minta o token
    token.mint(to, amount);
    
    emit Transfer(
      msg.sender,
      to,
      amount,
      block.timestamp,
      otherChainNonce,
      Step.Mint
    );
  }
}
